/// <summary>
///     Структура описывает игральную карту
/// </summary>
public struct Card
{
    public string Name;
    public Sprite Logo;
    public DateTime Date;
    public string Description;

    /// <summary>
    ///     конструктор класса Карт
    /// </summary>
    /// <param name="name"> название карты</param>
    /// <param name="logoPath">путь к картинке</param>
    /// <param name="date">дата события</param>
    /// <param name="description">описание события</param>
    public Card(string name, string logoPath, DateTime date, string description)
    {
        Name = name;
        Logo = Resources.Load<Sprite>(logoPath);
        Date = date;
        Description = description;
    }

    public override string ToString() => $"{Date:dd.MM.yyyy};{Name};{Description}";
}

