        CardManager.GeneralHistoryXVIIICentury.Add(new Card("Конец Войны за независимость североамериканских колоний Англии (США)", string.Empty, new DateTime(1783, 01, 01), "Война за независимость США - война Великобритании и американских лоялистов против революционеров Тринадцати британских колоний, которые провозгласили независимость от Великобритании. \r\n Стороны: 13 колоний (США), Франция, Испания, Голландия VS Британская империя, лоялисты.\r\nПричина: Налоговая политика метрополии в Тринадцати колониях.\r\nИтог: Победа США; Парижский мир."));
        CardManager.GeneralHistoryXVIIICentury.Add(new Card("Включение Крыма в состав Российской империи", string.Empty, new DateTime(1783, 01, 01), "Императрицей Екатериной II Великой был подписан Манифест о присоединении к Российской империи Крымского полуострова."));
        CardManager.GeneralHistoryXVIIICentury.Add(new Card("Создание Дж. Уаттом паровой машины", string.Empty, new DateTime(1784, 01, 01), "Джеймс Уатт - шотландский инженер, изобретатель-механик. Ввёл первую единицу мощности - лошадиную силу. Его именем названа единица мощности - Ватт. Усовершенствовал паровую машину Ньюкомена. Создал универсальную паровую машину двойного действия. Изобретения Уатта запустили процесс промышленной революции в Англии, а затем и во всём мире."));
        CardManager.GeneralHistoryXVIIICentury.Add(new Card("Конец правления Фридриха II в Пруссии", string.Empty, new DateTime(1786, 01, 01), "Фридрих II (Фридрих Великий) - король Пруссии, яркий представитель просвещённого абсолютизма, основоположник прусско-германской государственности."));
        CardManager.GeneralHistoryXVIIICentury.Add(new Card("Принятие Конституции США", string.Empty, new DateTime(1787, 01, 01), "Конституция США - основной закон США, имеющий высшую юридическую силу. Конституция США была принята на Конституционном Конвенте в Филадельфии и впоследствии ратифицирована всеми тринадцатью существовавшими тогда американскими штатами. В основе Конституции США лежит принцип разделения властей между законодательной (конгресс), исполнительной (президент) и судебной (верховный суд и нижестоящие суды) ветвями."));
        CardManager.GeneralHistoryXVIIICentury.Add(new Card("Начало работы Генеральных штатов во Франции", string.Empty, new DateTime(1789, 01, 01), "Генеральные штаты во Франции - высшее совещательное учреждение сословного представительства Франции до XIX века. Генеральные штаты являлись представительным органом, созываемым по инициативе королевской власти в критические моменты для оказания помощи правительству."));
        CardManager.GeneralHistoryXVIIICentury.Add(new Card("Депутаты Генеральных штатов от третьего сословия объявили себя Национальным собранием", string.Empty, new DateTime(1789, 01, 01), "Национальное собрание (Национальная ассамблея) - нижняя палата парламента Франции. Заседает Национальное собрание в Бурбонском дворце (Пале-Бурбон) Парижа."));
        CardManager.GeneralHistoryXVIIICentury.Add(new Card("Начало работы Учредительного собрания во Франции", string.Empty, new DateTime(1789, 01, 01), "Учредительное собрание  - первое учредительное собрание представителей французского народа, организованное депутатами французских генеральных штатов в начале Великой Французской революции."));
        CardManager.GeneralHistoryXVIIICentury.Add(new Card("Взятие Бастилии в Париже", string.Empty, new DateTime(1789, 01, 01), "Взятие Бастилии - одно из важнейших событий Великой французской революции, штурм крепости-тюрьмы Бастилия, являющейся символом всевластия королей."));
        CardManager.GeneralHistoryXVIIICentury.Add(new Card("Принятие Учредительным собранием во Франции Декларации прав человека и гражданина", string.Empty, new DateTime(1789, 01, 01), "Декларация прав человека и гражданина - важнейший документ Великой французской революции, определяющий индивидуальные права человека. В основу идей Декларации прав человека и гражданина положена концепция равноправия и свободы, принадлежащей каждому от рождения."));
        CardManager.GeneralHistoryXVIIICentury.Add(new Card("Начало правления османского султана Селима III", string.Empty, new DateTime(1789, 01, 01), "Селим III - 28-й султан Османской империи, сын султана Мустафы III. Пытался модернизировать страну, опираясь на европейские примеры, проводил либеральные реформы, ограничил вольности и привилегии корпуса янычар. Увлекался светским искусством и поэзией. Был свергнут янычарами. Во время бунта, приведшего к свержению Мустафы IV, Селим был убит по приказу султана."));
        CardManager.GeneralHistoryXVIIICentury.Add(new Card("Конец правления в монархии Габсбургов Иосифа II", string.Empty, new DateTime(1790, 01, 01), "Иосиф II - король Германии, избран императором Священной Римской империи, старший сын Марии Терезии, до конца её смерти был её соправителем. После смерти матери унаследовал от неё владения Габсбургов - эрцгерцогство Австрийское, королевства Богемское и Венгерское. Выдающийся государственный деятель, реформатор, яркий представитель эпохи просвещённого абсолютизма."));
        CardManager.GeneralHistoryXVIIICentury.Add(new Card("Начало работы Законодательного собрания во Франции", string.Empty, new DateTime(1791, 01, 01), "Законодательное собрание - представительное учреждение с законодательной властью, установленное первой французской Конституцией. Этому собранию конституция давала право издавать законы, вотировать налоги, определять количество сухопутных и морских сил, объявлять войну, ратифицировать мирные и коммерческие трактаты и возбуждать перед верховным судом преследование против министров, которые сами не могли быть членами законодательного собрания."));
        CardManager.GeneralHistoryXVIIICentury.Add(new Card("Принятие первой в истории Франции Конституции", string.Empty, new DateTime(1791, 01, 01), "Конституция Франции - основной закон Французской республики."));
        CardManager.GeneralHistoryXVIIICentury.Add(new Card("Восстание и крушение монархии во Франции", string.Empty, new DateTime(1792, 01, 01), "Восстание 10 августа 17** года - одно из знаковых событий Великой Французской революции. День 10 августа привёл к фактическому падению французской монархии после штурма Тюильри Национальной гвардией Парижской коммуны и революционными федератами. Король Людовик XVI был отстранён от власти и арестован. \r\n Стороны: роялисты VS республиканцы. \r\nИтог: Падение и отмена конституционной монархии, установление первой республики"));
        CardManager.GeneralHistoryXVIIICentury.Add(new Card("Начало революционных войн во Франции", string.Empty, new DateTime(1792, 01, 01), "Французские революционные войны - серия конфликтов с участием Французской республики, проходивших в Европе в период с объявления Францией войны Австрии до заключения Амьенского мира. \r\nСтороны: Франция VS Священная Римская империя, Австрия, Пруссия, Великобритания, Росиия, Испания, французские роялисты, Португалия. \r\nПричина: Великая Французская революция, агрессивная экспансия Франции\r\nИтог: победа Франции, Люневильский мир, Амьенский мир"));
        CardManager.GeneralHistoryXVIIICentury.Add(new Card("Смерть Людовика XVI", string.Empty, new DateTime(1793, 01, 01), "Людовик XVI - король Франции из династии Бурбонов. Последний монарх Франции Старого порядка. При нём после созыва Генеральных штатов в 1789 году началась Великая французская революция. Людовик неудачно пытался бежать из страны, после чего сначала принял конституцию 1791 года, отказался от абсолютизма и стал конституционным монархом, однако вскоре начал нерешительно противодействовать радикальным мерам революционеров."));
        CardManager.GeneralHistoryXVIIICentury.Add(new Card("Приход к власти якобинцев", string.Empty, new DateTime(1793, 01, 01), "Якобинцы - участники Якобинского клуба - самое известное и влиятельное политическое движение Великой Французской революции, связанное с определением радикального эгалитаризма, республиканизма и применения насилия в достижении целей, приведших к созданию революционного правительства."));
        CardManager.GeneralHistoryXVIIICentury.Add(new Card("Второй раздел Речи Посполитой (Польши)", string.Empty, new DateTime(1793, 01, 01), "Первый раздел Речи Посполитой - аннексия части земель Речи Посполитой Пруссией и Российской империей. Второй из трёх разделов Речи Посполитой, в результате которых она прекратила своё существование."));
        CardManager.GeneralHistoryXVIIICentury.Add(new Card("Переворот 9 термидора (Термидорианский переворот)", string.Empty, new DateTime(1794, 01, 01), "Термидорианский переворот - государственный переворот, произошедший во Франции и ставший одним из ключевых событий Великой французской революции. Переворот привёл к аресту и казни лидера якобинцев Максимильена Робеспьера и концу революционных преобразований."));
        CardManager.GeneralHistoryXVIIICentury.Add(new Card("Конец правления Цяньлуна в цинском Китае", string.Empty, new DateTime(1795, 01, 01), "Айсиньгьоро Хунли - шестой маньчжурский правитель империи Цин. В течение 59 лет правил под девизом “Цяньлун”. Четвёртый сын Айсиньгьоро Иньчжэня, правившего под девизом “Юнчжэн”. Полководец."));
        CardManager.GeneralHistoryXVIIICentury.Add(new Card("Учреждение Директории и принятие республиканской конституции", string.Empty, new DateTime(1795, 01, 01), "Директория - высший орган государственной власти Французской республики во время заключительного периода Великой Французской революции. Директория представляла собой коллективного главу государства в лице пяти директоров, избиравшихся Советом старейшин."));
        CardManager.GeneralHistoryXVIIICentury.Add(new Card("Третий раздел Речи Посполитой (Польши)", string.Empty, new DateTime(1795, 01, 01), "Третий раздел Речи Посполитой - аннексия части земель Речи Посполитой тремя соседними с ней государствами - Пруссией, Австрией и Российской империей. Последний из трёх разделов Речи Посполитой, в результате которых она прекратила своё существование."));
        CardManager.GeneralHistoryXVIIICentury.Add(new Card("Конец правления Екатерины II в России", string.Empty, new DateTime(1796, 01, 01), "Екатерина II (Екатерина Алексеевна) (Екатерина Великая) - императрица и Самодержица Всероссийская. Монарх просвещённого абсолютизма. Взошла на престол в результате дворцового переворота против своего мужа Петра III. Екатерининская эпоха ознаменовалась максимальным закрепощением крестьян и всесторонним расширением привилегий дворянства. При Екатерине Великой границы Российской империи были значительно раздвинуты на запад и на юг. Система государственного управления при Екатерине Второй впервые со времени Петра I была реформирована."));
        CardManager.GeneralHistoryXVIIICentury.Add(new Card("Начало итальянского похода Наполеона Бонапарта", string.Empty, new DateTime(1796, 01, 01), "Первая Итальянская кампания (Первый итальянский поход) - поход французских революционных войск в итальянские земли во главе с Наполеоном Бонапартом. Представляет собой один из важнейших эпизодов войн Франции с антифранцузскими коалициями."));
        CardManager.GeneralHistoryXVIIICentury.Add(new Card("Конец итальянского похода Наполеона Бонапарта", string.Empty, new DateTime(1797, 01, 01), "Первая Итальянская кампания (Первый итальянский поход) - поход французских революционных войск в итальянские земли во главе с Наполеоном Бонапартом. Представляет собой один из важнейших эпизодов войн Франции с антифранцузскими коалициями."));
        CardManager.GeneralHistoryXVIIICentury.Add(new Card("Начало Египетского похода Наполеона Бонапарта", string.Empty, new DateTime(1798, 01, 01), "Египетский поход - кампания, предпринятая по инициативе и под непосредственным руководством Наполеона Бонапарта, главной целью которой была попытка завоевания Египта."));
        CardManager.GeneralHistoryXVIIICentury.Add(new Card("Конец Египетского похода Наполеона Бонапарта", string.Empty, new DateTime(1799, 01, 01), "Египетский поход - кампания, предпринятая по инициативе и под непосредственным руководством Наполеона Бонапарта, главной целью которой была попытка завоевания Египта."));
        CardManager.GeneralHistoryXVIIICentury.Add(new Card("Государственный переворот Наполеона Бонапарта 18 брюмера (завершение революции во Франции)", string.Empty, new DateTime(1799, 01, 01), "Переворот 18 брюмера - государственный переворот во Франции, в результате которого была лишена власти Директория, разогнан парламент (Совет пятисот и Совет старейшин) и был установлен Консулат во главе с Наполеоном Бонапартом."));
        CardManager.GeneralHistoryXVIIICentury.Add(new Card("Итальянский и швейцарский походы А. В. Суворова", string.Empty, new DateTime(1799, 01, 01), "Итальянский поход Суворова - боевые действия русско-австрийской армии под командованием фельдмаршала графа А. В. Суворова против французских войск в Северной Италии. Поход являлся частью войны Второй коалиции против революционной Франции. \r\nШвейцарский поход Суворова - военная операция по переброске подкреплений из Италии в Швейцарию для усиления группировки войск Второй коалиции, предпринятая генерал-фельдмаршалом Александром Васильевичем Суворовым в ходе войны Второй коалиции против революционной Франции."));
        CardManager.GeneralHistoryXVIIICentury.Add(new Card("Конец правления османского султана Селима III", string.Empty, new DateTime(1808, 01, 01), "Селим III - 28-й султан Османской империи, сын султана Мустафы III. Пытался модернизировать страну, опираясь на европейские примеры, проводил либеральные реформы, ограничил вольности и привилегии корпуса янычар. Увлекался светским искусством и поэзией. Был свергнут янычарами. Во время бунта, приведшего к свержению Мустафы IV, Селим был убит по приказу султана."));

        //RussianHistoryXVIIICentury

        CardManager.RussianHistoryXVIIICentury.Add(new Card("Поражение России под Нарвой", string.Empty, new DateTime(1700, 01, 01), "Битва при Нарве - одно из первых сражений Великой Северной войны между русской армией Петра I и шведской армией Карла XII, у города Нарвы и окончившееся поражением русских войск. Русская армия под руководством царя Петра I смогла взять Нарву только при повторной осаде."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Начало Великой Северной войны", string.Empty, new DateTime(1700, 01, 01), "Великая Северная Война: \r\nПричина: Противоречия между Швецией и государствами Северной Европы из-за контроля над Балтийским морем и его побережьем \nИтог: Победа антишведской коалиции; Ништадтский мирный договор\r\nИзменения: Россия возвращает выход к Балтийскому морю и провозглашается империей; Швеция теряет много своих владений и утрачивает статус великой державы"));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Ништадский мир (конец Великой Северной войны)", string.Empty, new DateTime(1721, 01, 01), "Великая Северная Война: \r\nПричина: Противоречия между Швецией и государствами Северной Европы из-за контроля над Балтийским морем и его побережьем \nИтог: Победа антишведской коалиции; Ништадтский мирный договор\r\nИзменения: Россия возвращает выход к Балтийскому морю и провозглашается империей; Швеция теряет много своих владений и утрачивает статус великой державы"));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Основание Санкт-Петербурга", string.Empty, new DateTime(1703, 01, 01), "История Санкт-Петербурга начинается со строительства Петропавловской крепости. Основателем города является Пётр I - первый российский император. Он же сделал Санкт-Петербург новой столицей Русского царства, а затем и Российской империи."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Начало восстания в Астрахани", string.Empty, new DateTime(1705, 01, 01), "Астраханское восстание (Астраханский бунт) - восстание стрельцов, казаков, солдат, посадских, работных и беглых людей, произошедшее в городе Астрахань и его окрестностях в XVIII веке. К причинам восстания историки относят усиление произвола и насилия со стороны местной администрации, введение новых налогов и жестокость астраханского воеводы Тимофея Ржевского."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Начало восстания Кондратия Булавина", string.Empty, new DateTime(1707, 01, 01), "Булавинское восстание (Восстание Булавина) - казацкое восстание (бунт) в Русском царстве, названное в честь лидера К. А. Булавина. \r\nПричины: Попытки ограничить казачье самоуправление, принудительное использование людей на строительстве флота и крепостных сооружений, постоянные экспедиции по розыску беглых крестьян\r\nИтог: Подавление восстания правительственными войсками и казаками\r\nОрганизаторы: Кондратий Булавин\r\nДвижущие силы: донские казаки\r\nПротивники: Василий Долгоруков"));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Полтавская битва", string.Empty, new DateTime(1709, 01, 01), "Полтавская битва - генеральное сражение Северной войны между русскими войсками под командованием Петра I и шведской армией под командованием Карла XII. \r\nМесто: близ Полтавы, Войско Запорожское, Русское царство\r\nИтог: Решительная победа Русского войска"));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Прутский поход", string.Empty, new DateTime(1711, 01, 01), "Прутский поход - поход в Молдавию русской армии под предводительством Петра I против Османской империи в ходе Русско-турецкой войны. На реке Прут русская армия была прижата к правому берегу 120-тысячной турецкой армией и 70-тысячной конницей крымских татар. Безнадёжное положение армии заставило Петра пойти на переговоры, и в результате было заключено мирное соглашение, по которому к Турции отошли завоёванный в 1696 году Азов и побережье Азовского моря."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Учреждение Сената", string.Empty, new DateTime(1711, 01, 01), "Правительствующий сенат - высший государственный орган законосовещательной, исполнительной и судебной власти Российской империи, подчинённый императору и назначаемый им. Учреждён Петром Великим как высший орган государственной власти и законодательства."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Указ о единонаследии", string.Empty, new DateTime(1714, 01, 01), "Указ о единонаследии (О порядке наследования в движимых и недвижимых имуществах) - нормативно-правовой акт, подписанный Петром I. Указ регламентировал правовой статус дворянства и закреплял юридическое слияние таких форм земельной собственности, как вотчина и поместье. В указе о единонаследии впервые был использован термин “недвижимое имущество”. Обнародование указа датируется 23 марта 1714 года. Отменён императрицей Анной Иоанновной по докладу Сената."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Введение подушных переписей", string.Empty, new DateTime(1718, 01, 01), "Подушные переписи (“ревизии”) - переписи, проводившиеся на основе новой учётной единицы - мужских душ вместо “тяглового двора”, использовавшегося в Русском государстве в XVI-XVII веках. Всего было проведено 10 ревизий."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Сражение при Гренгаме", string.Empty, new DateTime(1720, 01, 01), "Сражение при Гренгаме - морское сражение, произошедшее в Балтийском море около острова Гренгам (южная группа Аландских островов), явилось последним крупным сражением Великой Северной войны."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Провозглашение России империей", string.Empty, new DateTime(1721, 01, 01), "Российская империя (Всероссийская империя) - государство, провозглашенное в связи с получением Петром I титула императора и существовавшее в до Февральской революции и провозглашения Временным правительством республики."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Создание Священного синода", string.Empty, new DateTime(1721, 01, 01), "Святейший правительствующий синод - высший орган церковно-государственного управления Русской церковью в синодальный период. Святейший синод был высшей административной и судебной инстанцией Русской церкви."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Введение Табели о рангах", string.Empty, new DateTime(1722, 01, 01), "Табель о рангах - таблица, содержащая перечень соответствий между военными, гражданскими и придворными чинами, ранжированными по 14 классам. Учреждена указом Петра I “Табель о рангах всех чинов, воинских, статских и придворных, которые в котором классе чины; и которые в одном классе, те имеют по старшинству времени вступления в чин между собою, однако ж воинские выше прочих, хотя б и старее кто в том классе пожалован был”."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Персидский поход", string.Empty, new DateTime(1722, 01, 01), "Персидский поход - поход русских армии и флота в принадлежавшие Персии юго-восточное Закавказье и Дагестан. Официальной целью похода было проложить торговый путь для российских купцов и оградить их от грабителей. Существует мнение, что целью также было наказание лезгин в Закавказье."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Начало правления Екатерины I", string.Empty, new DateTime(1725, 01, 01), "Екатерина I (Марта Самуиловна Скавронская) (Екатерина Алексеевна Михайлова) - императрица всероссийская сначала как супруга царствующего императора, а позже как правящая государыня; вторая жена Петра I, мать императрицы Елизаветы Петровны. В её честь Петром I учреждён орден Святой Екатерины и назван город Екатеринбург на Урале. Имя Екатерины I носит также Екатерининский дворец в Царском Селе (построенный при её дочери Елизавете Петровне)."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Конец правления Екатерины I", string.Empty, new DateTime(1727, 01, 01), "Екатерина I (Марта Самуиловна Скавронская) (Екатерина Алексеевна Михайлова) - императрица всероссийская сначала как супруга царствующего императора, а позже как правящая государыня; вторая жена Петра I, мать императрицы Елизаветы Петровны. В её честь Петром I учреждён орден Святой Екатерины и назван город Екатеринбург на Урале. Имя Екатерины I носит также Екатерининский дворец в Царском Селе (построенный при её дочери Елизавете Петровне)"));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Начало эпохи дворцовых переворотов", string.Empty, new DateTime(1725, 01, 01), "Эпоха дворцовых переворотов - временной промежуток в политической жизни России XVIII столетия, когда переход высшей государственной власти происходил путём совершения гвардейцами или придворными дворцовых переворотов."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Конец эпохи дворцовых переворотов", string.Empty, new DateTime(1762, 01, 01), "Эпоха дворцовых переворотов - временной промежуток в политической жизни России XVIII столетия, когда переход высшей государственной власти происходил путём совершения гвардейцами или придворными дворцовых переворотов."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Начало правления Петра II", string.Empty, new DateTime(1727, 01, 01), "Пётр II Алексеевич - император всероссийский, сменивший на престоле Екатерину I. Внук Петра I, сын царевича Алексея Петровича, последний представитель рода Романовых по прямой мужской линии. Вступил на престол когда ему было всего 11 лет, и умер в 14 лет от оспы. Пётр не успел проявить интереса к государственным делам и самостоятельно фактически не правил. Реальная власть в государстве находилась в руках Верховного тайного совета и в особенности фаворитов юного императора, сначала А. Д. Меншикова, после его низложения - Долгоруковых."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Конец правления Петра II", string.Empty, new DateTime(1730, 01, 01), "Пётр II Алексеевич - император всероссийский, сменивший на престоле Екатерину I. Внук Петра I, сын царевича Алексея Петровича, последний представитель рода Романовых по прямой мужской линии. Вступил на престол когда ему было всего 11 лет, и умер в 14 лет от оспы. Пётр не успел проявить интереса к государственным делам и самостоятельно фактически не правил. Реальная власть в государстве находилась в руках Верховного тайного совета и в особенности фаворитов юного императора, сначала А. Д. Меншикова, после его низложения - Долгоруковых."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Начало правления Анны Иоанновны", string.Empty, new DateTime(1730, 01, 01), "Анна Иоанновна (Анна Ивановна) - императрица Всероссийская из династии Романовых. Средняя из трёх выживших дочерей царя Ивана V Алексеевича. После смерти своего двоюродного племянника Петра II была приглашена на российский престол Верховным тайным советом как монарх с ограниченными (в пользу аристократов - “верховников”) полномочиями. При поддержке дворян восстановила абсолютизм, распустив Верховный тайный совет. Время её правления позднее получило название “бироновщина” по имени её фаворита Эрнста Бирона."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Конец правления Анны Иоанновны", string.Empty, new DateTime(1740, 01, 01), "Анна Иоанновна (Анна Ивановна) - императрица Всероссийская из династии Романовых. Средняя из трёх выживших дочерей царя Ивана V Алексеевича. После смерти своего двоюродного племянника Петра II была приглашена на российский престол Верховным тайным советом как монарх с ограниченными (в пользу аристократов - “верховников”) полномочиями. При поддержке дворян восстановила абсолютизм, распустив Верховный тайный совет. Время её правления позднее получило название “бироновщина” по имени её фаворита Эрнста Бирона."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Начало войны за Польское наследство", string.Empty, new DateTime(1733, 01, 01), "Война за польское наследство - война, происходившая между коалициями России, Австрии и Саксонии с одной стороны и Франции, Испании и Сардинского королевства с другой. Поводом послужили выборы короля на польский престол после смерти Августа II. \r\nИтог: Венский мирный договор\r\nИзменения: Август III признан королём Речи Посполитой; Франция признала Прагматическую санкцию"));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Конец войны за Польское наследство", string.Empty, new DateTime(1735, 01, 01), "Война за польское наследство - война, происходившая между коалициями России, Австрии и Саксонии с одной стороны и Франции, Испании и Сардинского королевства с другой. Поводом послужили выборы короля на польский престол после смерти Августа II. \r\nИтог: Венский мирный договор\r\nИзменения: Август III признан королём Речи Посполитой; Франция признала Прагматическую санкцию"));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Начало второй Русско-турецкой войны XVIII века", string.Empty, new DateTime(1735, 01, 01), "Русско-турецкая война - война между Российской (в союзе со Священной Римской Империей) и Османской империями, вызванная возросшими противоречиями в связи с Войной за польское наследство, а также с продолжавшимися набегами крымских татар на Новороссию. \r\nИтог: Незначительные территориальные уступки Османской империи на русском фронте, османская победа над Габсбургами, Белградский мир\r\nИзменения: Присоединение к России Азова и Запорожья; присоединение к Османской империи Сербии и Олтении (Малой Валахии)"));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Белградский мирный договор (конец второй Русско-турецкой войны XVIII века)", string.Empty, new DateTime(1739, 01, 01), "Русско-турецкая война - война между Российской (в союзе со Священной Римской Империей) и Османской империями, вызванная возросшими противоречиями в связи с Войной за польское наследство, а также с продолжавшимися набегами крымских татар на Новороссию. \r\nИтог: Незначительные территориальные уступки Османской империи на русском фронте, османская победа над Габсбургами, Белградский мир\r\nИзменения: Присоединение к России Азова и Запорожья; присоединение к Османской империи Сербии и Олтении (Малой Валахии)"));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Начало правления Ивана VI", string.Empty, new DateTime(1740, 01, 01), "Иван VI (Иоанн Антонович) - император всероссийский из династии Романовых. Царствовал во время Эпохи дворцовых переворотов. Является правнуком Ивана V. Формально царствовал первый год своей жизни при регентстве сначала Бирона, а затем собственной матери Анны Леопольдовны. Император-младенец был свергнут Елизаветой Петровной, провёл почти всю жизнь в одиночном заключении и уже в царствование Екатерины II был убит охраной в 23-летнем возрасте при попытке его освободить."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Конец правления Ивана VI", string.Empty, new DateTime(1741, 01, 01), "Иван VI (Иоанн Антонович) - император всероссийский из династии Романовых. Царствовал во время Эпохи дворцовых переворотов. Является правнуком Ивана V. Формально царствовал первый год своей жизни при регентстве сначала Бирона, а затем собственной матери Анны Леопольдовны. Император-младенец был свергнут Елизаветой Петровной, провёл почти всю жизнь в одиночном заключении и уже в царствование Екатерины II был убит охраной в 23-летнем возрасте при попытке его освободить."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Начало первой Русско-шведской войны XVIII века", string.Empty, new DateTime(1741, 01, 01), "Русско-шведская война - реваншистская война, которую Швеция начала в надежде вернуть себе утраченные в ходе Северной войны территории. Итог: победа России, Абоский мир"));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Конец первой Русско-шведской войны XVIII века", string.Empty, new DateTime(1743, 01, 01), "Русско-шведская война - реваншистская война, которую Швеция начала в надежде вернуть себе утраченные в ходе Северной войны территории. Итог: победа России, Абоский мир"));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Начало правления Елизаветы Петровны", string.Empty, new DateTime(1741, 01, 01), "Елизавета Петровна - императрица и самодержица Всероссийская из династии Романовых, младшая дочь Петра I и Екатерины I. Правление Елизаветы Петровны отмечено возрождением традиций и идеалов петровского периода, восстановлением Правительствующего сената, учреждением Московского университета, постройкой грандиозных дворцов и роскошью при дворе, появлением первого банка в Российской империи. В елизаветинскую эпоху Российская империя успешно воевала в войне за австрийское наследство, русско-шведской войне и Семилетней войне. Продолжилось исследование и освоение Сибири."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Конец правления Елизаветы Петровны", string.Empty, new DateTime(1761, 01, 01), "Елизавета Петровна - императрица и самодержица Всероссийская из династии Романовых, младшая дочь Петра I и Екатерины I. Правление Елизаветы Петровны отмечено возрождением традиций и идеалов петровского периода, восстановлением Правительствующего сената, учреждением Московского университета, постройкой грандиозных дворцов и роскошью при дворе, появлением первого банка в Российской империи. В елизаветинскую эпоху Российская империя успешно воевала в войне за австрийское наследство, русско-шведской войне и Семилетней войне. Продолжилось исследование и освоение Сибири."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Основание Московского университета", string.Empty, new DateTime(1755, 01, 01), "Императорский Московский университет - старейший из университетов Российской империи, учреждённый указом императрицы Елизаветы Петровны “Об учреждении Московского университета и двух гимназий”, согласно проекту, выработанному меценатом и вельможей И. И. Шуваловым при участии М. В. Ломоносова."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Начало правления Петра III", string.Empty, new DateTime(1761, 01, 01), "Пётр III Фёдорович - император всероссийский, первый представитель Гольштейн-Готторпской династии на российском престоле. Внук Петра I. После полугодового царствования свергнут в результате дворцового переворота, возвёдшего на престол его жену Екатерину II, и вскоре лишился жизни. Личность и деятельность Петра III долгое время расценивались историками единодушно отрицательно, однако затем появился и более взвешенный подход, отмечающий ряд государственных заслуг императора, который продолжал внутреннюю политику Петра I."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Конец правления Петра III", string.Empty, new DateTime(1762, 01, 01), "Пётр III Фёдорович - император всероссийский, первый представитель Гольштейн-Готторпской династии на российском престоле. Внук Петра I. После полугодового царствования свергнут в результате дворцового переворота, возвёдшего на престол его жену Екатерину II, и вскоре лишился жизни. Личность и деятельность Петра III долгое время расценивались историками единодушно отрицательно, однако затем появился и более взвешенный подход, отмечающий ряд государственных заслуг императора, который продолжал внутреннюю политику Петра I."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Манифест о вольности дворянства", string.Empty, new DateTime(1762, 01, 01), "Манифест о вольности дворянства - один из ключевых законодательных актов царствования Петра III. По данному законодательному акту впервые в истории России дворяне освобождались от обязательной гражданской и военной службы, могли по своему желанию выходить в отставку и беспрепятственно выезжать за границу. Основные положения манифеста Петра III были подтверждены Екатериной II в “Жалованной грамоте дворянству”."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Начало правления Екатерины II", string.Empty, new DateTime(1762, 01, 01), "Екатерина II (Екатерина Алексеевна) (Екатерина Великая) - императрица и Самодержица Всероссийская. Монарх просвещённого абсолютизма. Взошла на престол в результате дворцового переворота против своего мужа Петра III. Екатерининская эпоха ознаменовалась максимальным закрепощением крестьян и всесторонним расширением привилегий дворянства. При Екатерине Великой границы Российской империи были значительно раздвинуты на запад и на юг. Система государственного управления при Екатерине Второй впервые со времени Петра I была реформирована."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Конец правления Екатерины II", string.Empty, new DateTime(1796, 01, 01), "Екатерина II (Екатерина Алексеевна) (Екатерина Великая) - императрица и Самодержица Всероссийская. Монарх просвещённого абсолютизма. Взошла на престол в результате дворцового переворота против своего мужа Петра III. Екатерининская эпоха ознаменовалась максимальным закрепощением крестьян и всесторонним расширением привилегий дворянства. При Екатерине Великой границы Российской империи были значительно раздвинуты на запад и на юг. Система государственного управления при Екатерине Второй впервые со времени Петра I была реформирована."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Начало третьей Русско-турецкой войны XVIII века", string.Empty, new DateTime(1768, 01, 01), "Русско-турецкая война - одна из ключевых по значению войн между Российской и Османской империями. Основной целью войны со стороны России являлось получение выхода к Чёрному морю, Турция рассчитывала получить обещанные ей Барской конфедерацией Подолию и Волынь, расширить свои владения в Северном Причерноморье и на Кавказе, и установить протекторат над Речью Посполитой. \r\n Причина: Столкновение российских и франко-турецких интересов в Речи Посполитой\r\nИтог: Победа России; Кючук-Кайнарджийский мир\r\nИзменения: Россия получила выход к Черному морю; Россия получила право держать военный и торговый флот в Азовском и Чёрном морях; Независимость Крымского ханства"));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Кючук-Кайнарджийский мирный договор ((конец третьей Русско-турецкой войны XVIII века)", string.Empty, new DateTime(1774, 01, 01), "Русско-турецкая война - одна из ключевых по значению войн между Российской и Османской империями. Основной целью войны со стороны России являлось получение выхода к Чёрному морю, Турция рассчитывала получить обещанные ей Барской конфедерацией Подолию и Волынь, расширить свои владения в Северном Причерноморье и на Кавказе, и установить протекторат над Речью Посполитой. \r\n Причина: Столкновение российских и франко-турецких интересов в Речи Посполитой\r\nИтог: Победа России; Кючук-Кайнарджийский мир\r\nИзменения: Россия получила выход к Черному морю; Россия получила право держать военный и торговый флот в Азовском и Чёрном морях; Независимость Крымского ханства"));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Начало восстания Емельяна Пугачева", string.Empty, new DateTime(1773, 01, 01), "Восстание Пугачёва - восстание яицких казаков, переросшее в полномасштабную войну казаков, крестьян и народов Урала и Поволжья с правительством императрицы Екатерины II. Итог: Подавление восстания; Правительственные уступки казачеству, народам Поволжья и Урала, частично крестьянству, кроме крепостных"));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Начало губернской реформы", string.Empty, new DateTime(1775, 01, 01), "Областная (Губернская) реформа Екатерины II - преобразование провинциального управления в наместничествах. Её цель - усиление правительственного административно-полицейского контроля на местах, введение нового административно-территориального деления, в том числе в связи с расширением территории России, создание новых учреждений и модернизацию сословно-социальных отношений."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Присоединение Крыма к России", string.Empty, new DateTime(1783, 01, 01), "Присоединение Крыма к Российской империи - аннексия территории Крымского ханства Российской империей в ходе военной кампании Потёмкина по “усмирению Крыма”, после отречения последнего крымского хана Шахина Гирея под давлением представителей Потёмкина. На присоединённой территории в 1784 году была образована Таврическая область. Екатерина II подписала манифест “О принятии полуострова Крымского, острова Тамана и всей Кубанской стороны под Российскую державу”, над которым она работала совместно с Потёмкиным."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Жалованная грамота дворянству", string.Empty, new DateTime(1785, 01, 01), "Жалованная грамота дворянству - грамота Екатерины II, освободившая дворян от обязательной службы. Жалованная грамота дворянству завершила правовую консолидацию этого сословия, начатую Петром I (Указ о единонаследии). Грамота, вслед за Манифестом Петра III, предоставила дворянам возможности развития их творческих и управленческих способностей, позволила ощутить себя реальной движущей силой общества."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Начало пятой Русско-турецкой войны XVIII века", string.Empty, new DateTime(1787, 01, 01), "Русско-турецкая война - война между Российской империей и Священной Римской империей, с одной стороны, и Османской империей - с другой. Блистательная Порта планировала в этой войне вернуть себе земли, отошедшие к Российской империи в ходе одной из прошлых Русско-турецких войн, а также присоединённый к Российской империи Крым. Война закончилась победой Российской империи и заключением Ясского мира."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Ясский мирный договор (конец пятой Русско-турецкой войны XVIII века)", string.Empty, new DateTime(1791, 01, 01), "Русско-турецкая война - война между Российской империей и Священной Римской империей, с одной стороны, и Османской империей - с другой. Блистательная Порта планировала в этой войне вернуть себе земли, отошедшие к Российской империи в ходе одной из прошлых Русско-турецких войн, а также присоединённый к Российской империи Крым. Война закончилась победой Российской империи и заключением Ясского мира."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Взятие Измаила", string.Empty, new DateTime(1790, 01, 01), "Штурм Измаила - осада и штурм турецкой крепости Измаил русскими войсками под командованием генерал-аншефа А. В. Суворова, в ходе пятой русско-турецкой войны XVIII века."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Начало правления Павла I", string.Empty, new DateTime(1796, 01, 01), "Павел I Петрович - император Всероссийский, великий магистр Мальтийского ордена. Представитель династии Гольштейн-Готторп-Романовых, сын Екатерины II и Петра III."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Конец правления Павла I", string.Empty, new DateTime(1801, 01, 01), "Павел I Петрович - император Всероссийский, великий магистр Мальтийского ордена. Представитель династии Гольштейн-Готторп-Романовых, сын Екатерины II и Петра III."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Разделение России на губернии", string.Empty, new DateTime(1708, 01, 01), "Губернская (областная) реформа Петра I - реформа местного управления. До этой реформы года территория Российского государства делилась на различные по размеру и статусу уезды и разряды. Губернская реформа Петра I придала государственной жизни России бюрократический характер западноевропейского толка. Реформа сыграла значительную роль для победы в Северной войне, наметила разделение судебной и административной властей, военной и гражданской службы. Однако значительно увеличивался штат государственных служащих, а значит, и затраты на их содержание."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Гангутское сражение", string.Empty, new DateTime(1714, 01, 01), "Гангутское сражение - морское сражение Северной войны, состоявшееся у мыса Гангут в Балтийском море между русским армейским флотом и шведским отрядом из 10 судов, первая в истории России морская победа русского флота."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Начало создания коллегий", string.Empty, new DateTime(1718, 01, 01), "Коллегии - центральные органы отраслевого управления в Российской империи, сформированные в Петровскую эпоху взамен утратившей своё значение системы приказов."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Учреждение Академии наук в Петербурге", string.Empty, new DateTime(1725, 01, 01), "Петербургская академия наук (Императорская академия наук) - первое высшее научное учреждение Российской империи, основаннное указом Петра I."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Начало Семилетней войны", string.Empty, new DateTime(1756, 01, 01), "Семилетняя война: \r\n Стороны: Пруссия, Великобритания, Ирландия VS Священная Римская империя, монархия Габсбургов, Саксония, Франция, Российская Империя, Швеция, Испания\r\n Причина: Усиление Пруссии в Европе; Франко-английское колониальное соперничество\r\nИтог: Петербургский мир,Гамбургский мир,Парижский мир,Губертусбургский мир\r\nИзменения: Возвышение Пруссии; Великобритания вырвалась вперёд в колониальной гонке"));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Конец Семилетней войны", string.Empty, new DateTime(1763, 01, 01), "Семилетняя война: \r\n Стороны: Пруссия, Великобритания, Ирландия VS Священная Римская империя, монархия Габсбургов, Саксония, Франция, Российская Империя, Швеция, Испания\r\n Причина: Усиление Пруссии в Европе; Франко-английское колониальное соперничество\r\nИтог: Петербургский мир,Гамбургский мир,Парижский мир,Губертусбургский мир\r\nИзменения: Возвышение Пруссии; Великобритания вырвалась вперёд в колониальной гонке"));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Чесменское сражение", string.Empty, new DateTime(1770, 01, 01), "Чесменское сражение - состоялось в Чесменской бухте и возле нее между российским и турецким флотами. Сражение было частью Второго Пелопоннесского восстания, предшественником последующей греческой войны за независимость и важным эпизодом Русско-турецкой войны 1768-1774 годов."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Учреждение “Духовного собрания магометанского закона”", string.Empty, new DateTime(1788, 01, 01), "Оренбургское магометанское духовное собрание - первая официальная организация мусульман России, созданная по указу Екатерины II. Была учреждена должность главы российских мусульман - муфтия, кандидатура которого после избрания мусульманским обществом должна была утверждаться императором. Местом собрания была Уфа."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Начало второй Русско-шведской войны XVIII века", string.Empty, new DateTime(1788, 01, 01), "Русско-шведская война:\r\nПричина: Стремление Швеции вернуть территории Финляндии и Карелии, отвоёванных Россией в ходе предыдущих войн.\r\nПротивники: королевство Швеция, королевство Пруссия, Османская империя, Британская империя VS Российская империя, Датско-норвежская уния \r\nИтог: Верельский мирный договор\r\nИзменения: территориальные изменения отсутствуют."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Конец второй Русско-шведской войны XVIII века", string.Empty, new DateTime(1790, 01, 01), "Русско-шведская война:\r\nПричина: Стремление Швеции вернуть территории Финляндии и Карелии, отвоёванных Россией в ходе предыдущих войн.\r\nПротивники: королевство Швеция, королевство Пруссия, Османская империя, Британская империя VS Российская империя, Датско-норвежская уния \r\nИтог: Верельский мирный договор\r\nИзменения: территориальные изменения отсутствуют."));
        CardManager.RussianHistoryXVIIICentury.Add(new Card("Итальянский и швейцарский походы А. В. Суворова", string.Empty, new DateTime(1799, 01, 01), "Итальянский поход Суворова - боевые действия русско-австрийской армии под командованием фельдмаршала графа А. В. Суворова против французских войск в Северной Италии. Поход являлся частью войны Второй коалиции против революционной Франции. \r\nШвейцарский поход Суворова -военная операция по переброске подкреплений из Италии в Швейцарию для усиления группировки войск Второй коалиции, предпринятая генерал-фельдмаршалом Александром Васильевичем Суворовым в ходе войны Второй коалиции против революционной Франции."));
    }
